sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	try {
		log.info("TM NeedIt script is runnning.");
		if (!("u_requested_for_email" in source)) {
			log.info("Fetching mail from DB");
			var splitName = source.u_requested_for.split(' ');
			var firstName = splitName[0];
			var lastName = splitName[1];

			var gr = new GlideRecord('sys_user');
			gr.addQuery('first_name', firstName);
			gr.addQuery('last_name', lastName);
			gr.query();
			if(gr.next()) // Warning, this does not handle homonymes
				target.u_requested_for_email = gr.getValue('email');
			else 
				target.u_requested_for_email = firstName + "." + lastName + "@test.fr";	
		}
	} catch(e) {
		log.info('Error in TM NeedIt => ' + e);
	}

})(source, target, map, log, action==="update");
